                                            <form class="elementor-form" method="post" name="New Form">
                                                <div class="elementor-form-fields-wrapper elementor-labels-above">
                                                    <div class="elementor-field-type-text elementor-field-group elementor-column elementor-field-group-nama elementor-col-100 elementor-field-required">
                                                        <input type="hidden" value="{{ $tamu }}" id="tamu_undangan">
                                                        <label for="form-field-nama" class="elementor-field-label">Upload Resi</label>
                                                        <input size="1" type="file" name="form_fields[nama]" id="form-field-nama" class="elementor-field elementor-size-sm  elementor-field-textual" accept="image/*" onchange="handleFileChange(this)" required="required" aria-required="true">
                                                    </div>
                                                </div>
                                            </form>
                                            <script>
                                                function handleFileChange(input) {
                                                    var tamuUndangan = document.getElementById('tamu_undangan').value;
                                                    if (input.files && input.files[0]) {
                                                        var file = input.files[0];
                                                        if (!file.type.startsWith('image/')) {
                                                            alert("File yang diunggah bukan gambar!");
                                                            input.value = ''; 
                                                            return;
                                                        }
                                                        var confirmation = confirm("Apakah Anda yakin ingin menggunakan file '" + file.name + "'?");
                                                        if (confirmation) {
                                                            var csrftoken = '{{ csrf_token() }}';
                                                            var payload = new FormData();
                                                                payload.append('_token', csrftoken)
                                                                payload.append('file', file);
                                                                payload.append('tamu', tamuUndangan);
                                                                var xhr = new XMLHttpRequest();
                                                                xhr.open('POST', '{{ url($pageUrl) }}/upload-gift', false);
                                                                xhr.send(payload);
                                                                xhr.onreadystatechange = function() {
                                                                    if(xhr.readyState == XMLHttpRequest.DONE)
                                                                    {
